import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Form, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useState } from 'react';

const Register= () => {

    const [data, setData] = useState({
        email:"",
        pass:"",
        fname:"",
        confpass : "",
        lname:"",
    })

    const [errors, setErrors] = useState("")


    const handleChange=(e)=>{
        setData({ ...data, [e.target.name] : e.target.value});
    }

    const handleSubmit=e=>{
        e.preventDefault();

        console.log(e.data);
    }

    const check=e=>{
        setErrors({...errors, passerror : ""})
        let pass = this.data.pass;
        let cpass = this.data.confpass;
        if(pass !== cpass) {
            this.data.errors.passerror = "passwords dont match";
        }

    }

    return (
        <Container>
    <Form>
  <Form.Group className="mb-4" controlId="formEmail">
    <Form.Label>Email address</Form.Label>
    <Form.Control 
        type="email" 
        name="email" 
        required="required"
        placeholder="Enter email" 
        onChange={handleChange}
        value = {data.email}
    />

  </Form.Group>

  <Form.Group className='mb-4' controlId="formFname">
      <Form.Label>First Name</Form.Label>
      <Form.Control 
        type = "text" 
        name = "fname"
        required="required" 
        placeholder='First Name'
        onChange={handleChange}
        value = {data.fname}
    />
  </Form.Group>

  <Form.Group className='mb-4' controlId="formLname">
      <Form.Label>Last Name</Form.Label>
      <Form.Control
        type = "text" 
        name = "lname" 
        required="required"
        placeholder='Last Name'
        onChange={handleChange}
        value = {data.lname}
    />
  </Form.Group>

  <Form.Group className="mb-4" controlId="formPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control 
        type="password" 
        name = "pass" 
        placeholder="Password"
        required="required"
        onChange={handleChange}
        value = {data.pass}
    />
    <Form.Text className = "text-danger">{this.errors.passerror}</Form.Text>
  </Form.Group>

  <Form.Group className="mb-4" controlId="formconfPassword">
    <Form.Label>Confirm Password</Form.Label>
    <Form.Control 
        type="password" 
        name = "confpass" 
        placeholder="Confirm Password"
        required="required"
        value = {data.confpass}
        onChange={handleChange}
        onKeyUp={check}

    />
  </Form.Group>



  <Button 
    className="mr-4" 
    variant="primary" 
    type="submit"
    onSubmit={handleSubmit}
    >
    Submit
  </Button>

  <Form.Text className="text-muted ms-4 mt-4">
      Already have an account? Login <Link className='ml-4 link-primary' to="/login">Here</Link>
    </Form.Text>

</Form>
    </Container>   
    );
}
export default Register
